<?php

namespace AppBundle\Entity;

use Doctrine\ORM\EntityRepository;
use Doctrine\ORM\Query;

/**
 * ActivityRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ActivityRepository extends EntityRepository
{

    public function countDatesByInterval($dateFrom, $dateTo, $queryString = null) {
        $querySearchDQL = null;
        $querySearch = null;
        if($queryString) {
            $querySearch = $this->searchQueryToQueryDoctrine($queryString, $dateFrom, $dateTo);
            $querySearchDQL = ' AND a IN('.$querySearch->getDQL().')';
        }

        $dateFrom = clone $dateFrom;
        $dateFrom = $dateFrom->modify("+ 1 day");

        $query = $this->getEntityManager()
                    ->createQuery('
                          SELECT DATE(a.executedAt) as date, at.name, COUNT(a) as total
                          FROM AppBundle:Activity a
                          LEFT JOIN a.tags at
                          WHERE a.executedAt >= :date_to AND a.executedAt <= :date_from'.$querySearchDQL.'
                          GROUP BY date, at.name
                          ORDER BY a.executedAt ASC
                      ')
                    ->setParameter('date_from', $dateFrom)
                    ->setParameter('date_to', $dateTo);

        if($querySearch) {
            foreach($querySearch->getParameters() as $p) {
                $query->setParameter($p->getName(), $p->getValue());
            }
        }

        return $query->getScalarResult();
    }

    public function findByDatesIntervalByDays($dateFrom, $dateTo, $queryString = null, $nbDaysMax = null) {
        $querySearchDQL = null;
        $querySearch = null;
        if($queryString) {
            $querySearch = $this->searchQueryToQueryDoctrine($queryString, $dateFrom, $dateTo);
            $querySearchDQL = ' AND a IN('.$querySearch->getDQL().')';
        }

        $dateFrom = $dateFrom->modify("+ 1 day");

        $query = $this->getEntityManager()
                    ->createQuery('
                          SELECT DATE(a.executedAt) as date
                          FROM AppBundle:Activity a
                          WHERE a.executedAt >= :date_to AND a.executedAt <= :date_from'.$querySearchDQL.'
                          GROUP BY date
                          ORDER BY a.executedAt DESC
                      ')
                    ->setParameter('date_from', $dateFrom)
                    ->setParameter('date_to', $dateTo);

        if($nbDaysMax) {
            $query->setMaxResults($nbDaysMax);
        }

        if($querySearch) {
            foreach($querySearch->getParameters() as $p) {
                $query->setParameter($p->getName(), $p->getValue());
            }
        }

        $dates = $query->getScalarResult();

        if(!count($dates)) {

            return array();
        }

        $dateTo = new \DateTime($dates[count($dates) - 1]['date']);
        $dateTo = $dateTo->modify("+4 hours");
        $dateFrom = $dateFrom->modify("+4 hours");

        if($queryString) {
            $querySearch = $this->searchQueryToQueryDoctrine($queryString, $dateFrom, $dateTo);
            $querySearchDQL = ' AND a IN('.$querySearch->getDQL().')';
        }

        $query = $this->getEntityManager()
                    ->createQuery('
                    SELECT a, aa, at
                    FROM AppBundle:Activity a
                    LEFT JOIN a.attributes aa
                    LEFT JOIN a.tags at
                    WHERE a.executedAt >= :date_to AND a.executedAt <= :date_from'.$querySearchDQL.'
                    ORDER BY a.executedAt DESC
                      ')
                    ->setParameter('date_from', $dateFrom)
                    ->setParameter('date_to', $dateTo);

        if($querySearch) {
            foreach($querySearch->getParameters() as $p) {
                $query->setParameter($p->getName(), $p->getValue());
            }
        }

        return $query->getResult();
    }

    public function findByFilter($filter) {
        $querySearch = $this->searchQueryToQueryDoctrine($filter->getQuery());

        $query = $this->getEntityManager()
                    ->createQuery('
                          SELECT a
                          FROM AppBundle:Activity a
                          WHERE a NOT IN (SELECT asub FROM AppBundle:Activity asub JOIN asub.tags tsub WITH tsub = :tag)
                            AND a IN('.$querySearch->getDQL().')
                          ORDER BY a.executedAt DESC
                     ')
                    ->setParameter('tag', $filter->getTag());

        foreach($querySearch->getParameters() as $p) {
            $query->setParameter($p->getName(), $p->getValue());
        }

        return $query->getResult();
    }

    public function normalizeQuery($query) {
        $queryNormalized = null;
        $defaultOperator = " AND ";
        $operator = null;

        $mainParts = preg_split("/:/", $query);
        foreach($mainParts as $mainPart) {
            $parts = str_getcsv($mainPart, " ", '"');
            foreach($parts as $part) {
                if(!$part) {
                    continue;
                }
                if(in_array($part, array("OR", "AND"))) {
                    $operator = " ".$part." ";
                    continue;
                }

                if((in_array($operator, array(" OR ", " AND ")) || is_null($queryNormalized)) && in_array($part, array("NOT"))) {
                    $operator .= $part." ";
                    continue;
                }

                $queryNormalized .= $operator;
                $operator = $defaultOperator;

                $queryNormalized .= $part;
            }
            $operator = ":";
        }

        return $queryNormalized;
    }

    public function queryToArray($searchQuery) {
        $queryNormalized = $this->normalizeQuery($searchQuery);
        $terms = preg_split("/ (AND|OR) /", $queryNormalized);
        $params = array();
        foreach($terms as $term) {
            $termCleaned = preg_replace("/(^\(+|\)+$)/", "", trim($term));
            $termCleaned = preg_replace("/^NOT /", "", $termCleaned);
            $param = explode(":", $termCleaned);
            if(count($param) < 2) {
              $param[1] = $param[0];
              $param[0] = '*';
            }

            if(preg_match("/^[\(\)]*NOT /", $term)) {
                $param[2] = "not";
            }

            array_push($params, $param);
        }

        return $params;
    }

    public function queryToHierarchy($searchQuery) {
        $queryNormalized = $this->normalizeQuery($searchQuery);
        preg_match_all("/( AND | OR |\(|\))/", $queryNormalized, $matches);

        $operators = array();

        $bufferPrev = null;
        $bufferNext = null;
        foreach($matches[1] as $operator) {
            if($operator == "(") {
                $bufferPrev .= $operator;
                continue;
            }
            if($operator == ")") {
                $bufferNext .= $operator;
                continue;
            }
            if(count($operators)) {
                $operators[count($operators) - 1] .= $bufferNext;
            }
            $operators[] = $bufferPrev.strtolower(trim($operator));
            $bufferPrev = null;
            $bufferNext = null;
        }

        if(count($operators)) {
            $operators[count($operators) - 1] .= $bufferNext;
        }

        return $operators;
    }

    public function searchQueryToQueryDoctrine($searchQuery, $dateFrom = null, $dateTo = null, $prefix = 'aq') {
        $params = $this->queryToArray($searchQuery);
        $operators = $this->queryToHierarchy($searchQuery);
        $query = $this->getEntityManager()->createQueryBuilder()
                                 ->select($prefix)
                                 ->from('AppBundle:Activity', $prefix);

        $queriesFilter = array();
        $subqueriesFilter = array();
        foreach($params as $key => $param) {
            $name = $param[0];
            $value = str_replace('*', '%', $param[1]);
            $not = (isset($param[2]) && $param[2] == "not");

            if($not) {
                $subquery = $this->searchQueryToQueryDoctrine($name.":".$value, $dateFrom, $dateTo, $prefix.$key);
                $subqueriesFilter[] = $query->expr()->notIn($prefix.'.id', $subquery->getDQL());
                foreach($subquery->getParameters() as $p) {
                    $query->setParameter($p->getName(), $p->getValue());
                }
                $queriesFilter[] = "(%subquery".(count($subqueriesFilter)-1)."%)";
                continue;
            }

            $queryToAdd = null;

            if($name == 'title' || $name == 'content') {
                $queryToAdd = $query->expr()->like('('.$prefix.'.'.$name, ':'.$prefix.$key.'value)');
                $query->setParameter($prefix.$key.'value', $value);
            } elseif($name == 'tag') {
                $query
                    ->leftJoin($prefix.'.tags', $prefix.'t'.$key)
                    ->setParameter($prefix.$key.'value', $value);

                $queryToAdd = $query->expr()->like('('.$prefix.'t'.$key.'.name', ':'.$prefix.$key.'value)');
            } elseif($name == "*") {
                $keyJoin = uniqid();

                $query
                    ->leftJoin($prefix.'.attributes', $prefix.'a'.$keyJoin)
                    ->leftJoin($prefix.'.tags', $prefix.'t'.$keyJoin)
                    ->setParameter($prefix.$key.'value', "%".$value."%");

                $queryToAdd = $query->expr()->orX(
                        $query->expr()->like($prefix.'.title', ':'.$prefix.$key.'value'),
                        $query->expr()->like($prefix.'.content', ':'.$prefix.$key.'value'),
                        $query->expr()->like($prefix.'a'.$keyJoin.'.value', ':'.$prefix.$key.'value'),
                        $query->expr()->like($prefix.'t'.$keyJoin.'.name', ':'.$prefix.$key.'value')
                    );
            } else {
                $queryToAdd = $query->expr()->andX(
                    $query->expr()->like($prefix.'a'.$key.'.value', ':'.$prefix.$key.'value'),
                    $query->expr()->like($prefix.'a'.$key.'.name', ':'.$prefix.$key.'name')
                );
                $query->leftJoin($prefix.'.attributes', $prefix.'a'.$key)
                  ->setParameter($prefix.$key.'name', $name)
                  ->setParameter($prefix.$key.'value', $value);
            }

            $queriesFilter[] = $queryToAdd;
        }

        $query->andWhere(call_user_func_array(array($query->expr(), "andX"), $queriesFilter));
        $whereDQLOrigin = $query->getDQLPart("where");

        $whereDQLOrigin = preg_replace("/(^\(|\)$)/", "", $whereDQLOrigin);

        $whereParts = preg_split("/\) AND \(/", $whereDQLOrigin);


        $whereDQL = "";
        $startPrefix = null;
        $endPrefix = null;

        foreach($whereParts as $index => $wherePart) {
            if(isset($operators[$index])) {
                $startPrefix = preg_replace("/(or|and).*$/", "", $operators[$index]);
            }
            $whereDQL .= $startPrefix."(".trim($wherePart).")".$endPrefix;
            $startPrefix = null;
            $endPrefix = null;
            if(isset($operators[$index])) {
                $whereDQL .= " ".trim(strtoupper(preg_replace("/[()]+/", "", $operators[$index])))." ";
            }
            if(isset($operators[$index])) {
                $endPrefix = preg_replace("/^.*(or|and)/", "", $operators[$index]);
            }
        }

	    if($dateTo && $dateFrom) {
            $whereDQLDate = $prefix.".executedAt >= :date_to AND ".$prefix.".executedAt <= :date_from";

            $query->setParameter('date_from', $dateFrom)
                  ->setParameter('date_to', $dateTo);
        } else {
            $whereDQLDate = "1 = 1";
        }

        if($whereDQLDate && $whereDQL) {
            $whereDQL = "(".$whereDQLDate .") AND (". $whereDQL.")";
        } elseif($whereDQLDate) {
            $whereDQL = $whereDQLDate;
        }

        foreach($subqueriesFilter as $key => $subquery) {
            $whereDQL = str_replace("%subquery".$key."%", $subquery, $whereDQL);

        }

        $query->add("where", $whereDQL);

        return $query;
    }
}
